package springcontainer;import springcontainer.discount.RateDiscountPolicy;import springcontainer.member.MemberService;import springcontainer.member.MemberServiceImpl;import springcontainer.member.MemoryMemberRepository;import springcontainer.order.OrderService;import springcontainer.order.OrderServiceImpl;public class AppConfig {    public MemberService memberService() {        return new MemberServiceImpl(new MemoryMemberRepository());    }    public OrderService orderService() {        return new OrderServiceImpl(new MemoryMemberRepository(), new RateDiscountPolicy());    }}